mapper.select:
SELECT scheduleno, employeeno, work, work_startdate, work_enddate, start_time, end_time, visible
FROM SCHEDULE
WHERE scheduleno = #{scheduleno}

mapper.update:
UPDATE SCHEDULE
SET work = #{work, jdbcType = VARCHAR}, work_startdate = #{work_startdate, jdbcType = VARCHAR}, work_enddate= #{work_enddate, jdbcType = VARCHAR},
      start_time = #{start_time, jdbcType = VARCHAR}, end_time = #{end_time, jdbcType = VARCHAR}, visible = #{visible, jdbcType = VARCHAR}
WHERE scheduleno = #{scheduleno, jdbcType = NUMERIC}
